import React, { useEffect, useState} from 'react';
import history from '../history';
import { useDispatch, useSelector } from 'react-redux';
import './login.css'

//import { signin} from '../../actions/authActions';
//import NavBar from '../../navbar';

function Login(props) {
  const [Email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  //const userInstance = useSelector(state => state.user);
  //const { data, error} = userInstance;
  /*const dispatch = useDispatch();
  const login = (event) => {
    event.preventDefault();
    dispatch(signin(Email, password));
    if(error) {
      console.log("error");
    }

  }
  //useEffect triggers when side effects occur
  useEffect(() => {
    if(data) {
      history.push('/');
    }
    return () => {
    };
  }, [data]);*/
  return (   
    <div>
      <div id="container">
		<div className= "row justify-content-center">
          <div id="myform">
			<div className="col-6">
				<form className="login-form" action="#" method="post" autocomplete="off">
				  <p id="title">Log In To Your Account</p>
				  <div className="input-group input-group-sm">
					<div className="input-group-prepend">
					  <span className="input-group-text" id="basic-addon1"><svg width="100%" height="80px" viewBox="0 0 16 16" className="bi bi-person-fill" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
						  <path fill-rule="evenodd" d="M3 14s-1 0-1-1 1-4 6-4 6 3 6 4-1 1-1 1H3zm5-6a3 3 0 1 0 0-6 3 3 0 0 0 0 6z" />
						</svg></span>
					</div>
					<input id="email" type="text" className="form-control fix-rounded-right" required placeholder="user name or email" aria-label="user name or email" aria-describedby="basic-addon1"></input>
					<div className="invalid-feedback">
					  Please choose a username.
					</div>
				  </div>
				  <div className="input-group input-group-sm">
					<div className="input-group-prepend">
					  <span className="input-group-text" id="basic-addon2"><svg width="1em" height="1em" viewBox="0 0 16 16" className="bi bi-lock-fill" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
						  <path d="M2.5 9a2 2 0 0 1 2-2h7a2 2 0 0 1 2 2v5a2 2 0 0 1-2 2h-7a2 2 0 0 1-2-2V9z" />
						  <path fill-rule="evenodd" d="M4.5 4a3.5 3.5 0 1 1 7 0v3h-1V4a2.5 2.5 0 0 0-5 0v3h-1V4z" />
						</svg></span>
					</div>
					<input id="password" type="password" className="form-control fix-rounded-right" required placeholder="password:" aria-label="password:" aria-describedby="basic-addon2"></input>
					<div className="invalid-feedback">
					  Please enter your password.
					</div>
				  </div>
				  <a id="forgot" href="heeps://www.google.come">Forgot password?</a>
				  <button type="submit" onClick="onClickSignIn()">Login</button>
				  Need an account? <a href="signUp.html">Sign-Up</a>
				</form>
		  	</div>
			<div className="col-6">
	  			
	  		</div>
          </div>
          </div>
        </div>
    </div> 
  );
}
export default Login;
